parameters.functionCode:
// Параметры цикла
const start = 1;    // с какого числа
const end   = 1;    // до какого (включительно)
const step  = 1;    // шаг



// Генерируем items
const output = [];
for (let i = start; i <= end; i += step) {
  output.push({ json: { iteration: i } });
}

return output;

