const input = $json.message.text || '';
const regex = /(https?:\/\/(?:www\.)?(?:youtube\.com|youtu\.be|instagram\.com|instagr\.am)[^\s]*)/gi;

const matches = [...input.matchAll(regex)];

if (matches.length === 0) {
  return {
    json: {
      text: input.trim(),
      url: null,
      type: 'unknown'
    }
  };
}

const firstMatch = matches[0][0];
let type = '';

if (/youtube\.com|youtu\.be/.test(firstMatch)) {
  type = 'youtube';
} else if (/instagram\.com|instagr\.am/.test(firstMatch)) {
  type = 'instagram';
} else {
  type = 'unknown';
}

const cleanedText = input.replace(firstMatch, '').trim();

return {
  json: {
    text: cleanedText,
    url: firstMatch,
    type: type
  }
};
